import { useState } from 'react';
import './App.css';
import styled from "styled-components";
import Modal from "react-modal";

function App() {
  const selectList = ["리액트", "자바스크립트", "next.js", "타입스크립트"];

  const [name, setName] = useState();
  const [money, setMoney] = useState();
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selected, setSelected] = useState();


  const nameChangeHandler = (event) => {
    setName(event.target.value);
  }

  const priceChangeHandler = (e) => {
    const { value } = e.target;
    let str = value.replace(/[^0-9]/g, '')
    setMoney(str);
  }

  const SelectHandler = (e) => {
    setSelected(e.target.value);
  }

  const addComma = (price) => {
    let returnString = price?.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
    return returnString;
}

  // 저장누르면 alert 뜨는 버튼
  const clickAlertButtonHandler = (e) => {
  e.preventDefault();

  if ( name == "" || money == "" ){
    alert('이름이나 가격이 비어있습니다만')
  } else {
  alert(`{ 나에게 돈을 주시는 분 : ${name} , 나에게 줄 돈 : ${money}}`)
}

  setName('')
  setMoney('')
  };



  const alertBtn = () => {
    alert('왜 누르시고 그러세요');
  }

  const inputAlertBtn = () => {
    prompt('점심 메뉴 추천좀');
  }


  return (
    <div>
      <ItemDiv>
        <h1>Button</h1>
        <BtnDiv>
          <PrimaryBtn1 onClick={alertBtn}>
            <div>Large Primary Button
            </div>
          </PrimaryBtn1>
          <PrimaryBtn2>Medium</PrimaryBtn2>
          <PrimaryBtn3>Small</PrimaryBtn3>
        </BtnDiv>
        <BtnDiv>
          <NegativeBtn1 onClick={inputAlertBtn}>Large Negative Button</NegativeBtn1>
          <NegativeBtn2>Medium</NegativeBtn2>
          <NegativeBtn3>Small</NegativeBtn3>
        </BtnDiv>
      </ItemDiv>
      <br/>

      <ItemDiv>
        <h1>Input</h1>
        <div>
          <label>이름</label>
          <InputDiv type='text' value={name} onChange={nameChangeHandler}/>
          <label>가격</label>
          <InputDiv 
            type='text' 
            value={addComma(money) || ""} 
            placeholder='0'
            onChange={(e)=>priceChangeHandler(e)}/>
          <InputBtn onClick={clickAlertButtonHandler}>저장</InputBtn>
        </div>
      </ItemDiv>
      <br/>


      <ItemDiv>
        <h1>Modal</h1>
        <div>
          <button>open modal</button>
            <Modal isOpen={true}>
              하잇! ㅎㅎ
              <button onClick={()=> setM}>닫기</button>
              <button>확인</button>
            </Modal>
          <button>open modal</button>
        </div>
      </ItemDiv>
      <br/>

      <SelectDiv>
        <h1>Select</h1>
        <div>
          <select onChange={SelectHandler} value={selected}>
            {selectList.map((item)=>(
              <option value={item} key={item}>
                {item}
              </option>
            ))
            }
          </select>
          <select onChange={SelectHandler} value={selected}>
            {selectList.map((item)=>(
              <option value={item} key={item}>
                {item}
              </option>
            ))
            }
          </select>
        </div>
      </SelectDiv>
    </div>
  );
}

export default App;

// ItemDiv styled - components
const ItemDiv = styled.div`
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin: 0px 10px 0px 10px;
`

// Button styled - components
const BtnDiv = styled.div`
    display: flex;
    flex-direction: row;
    gap: 10px;
`

const PrimaryBtn1 = styled.button`
    cursor: pointer;
    border-radius: 8px;
    color: rgb(0, 0, 0);
    height: 50px;
    width: 200px;
    border: 3px solid #e0e094;
    background-color: #f5f5dc;
    font-weight: 600;
    &:active {
      background-color: #d4c78c;
    }
`

const PrimaryBtn2 = styled.button`
    cursor: pointer;
    border-radius: 8px;
    color: rgb(0, 0, 0);
    height: 45px;
    width: 150px;
    border: none;
    background-color: #f5f5dc;
    font-weight: 600;
    &:active {
      background-color: #d4c78c;
    }
`

const PrimaryBtn3 = styled.button`
    cursor: pointer;
    border-radius: 8px;
    color: rgb(0, 0, 0);
    height: 40px;
    width: 100px;
    border: none;
    background-color: #f5f5dc;
    font-weight: 600;
    &:active {
      background-color: #d4c78c;
    }
`

const NegativeBtn1 = styled.button`
    cursor: pointer;
    border-radius: 8px;
    color: rgb(0, 0, 0);
    height: 50px;
    width: 200px;
    border: 3px solid #bababa;
    background-color: #e9e9e9;
    font-weight: 600;
    &:active {
      background-color: #717171;
    }
`

const NegativeBtn2 = styled.button`
    cursor: pointer;
    border-radius: 8px;
    color: rgb(0, 0, 0);
    height: 45px;
    width: 150px;
    border: none;
    background-color: #e9e9e9;
    font-weight: 600;
    &:active {
      background-color: #717171;
    }
`

const NegativeBtn3 = styled.button`
    cursor: pointer;
    border-radius: 8px;
    color: rgb(0, 0, 0);
    height: 40px;
    width: 100px;
    border: none;
    background-color: #e9e9e9;
    font-weight: 600;
    &:active {
      background-color: #717171;
    }
`

// Input styled - components
const InputBtn = styled.button`
    cursor: pointer;
    border-radius: 8px;
    color: white;
    height: 40px;
    width: 100px;
    margin-left: 20px;
    border: none;
    background-color: #00498c;
    font-weight: 600;
    &:active {
    background-color: #003a70;
    }
`

const InputDiv = styled.input`
    height: 20px;
    width: 150px;
    border: 1px solid black;
    border-radius: 5px;
    padding: 10px;
    margin: 0px 10px 0px 10px;
`

// Modal styled - components


// Select styled - components
const SelectDiv = styled.div`
  width: 700px;
  height: 200px;
  border: 2px solid gray;
  border-radius: 8px;
  margin: 30px 10px 0px 10px;
`
