          <img src={garuimg} width={'400px'}/>
          <ModalBtn2 onClick={()=> setOpenModal(false)}>용서하기</ModalBtn2>
          </OpenModalDiv>
          </Modal>
        </div>
      </ItemDiv>
      <br/>

      <SelectDiv>
        <SelectDiv2>
          <h1>Select</h1>
          <div>
            <h3>매니저님이 수료후 저에게 사주실 메뉴는</h3>
            <HiddenBox>
              <Select onChange={SelectHandler} value={selected}>
                {selectList.map((item)=>(
                  <SelectOption value={item} key={item}>
                    {item}
                  </SelectOption>
                ))
                }
              </Select>
            </HiddenBox>
          </div>
        </SelectDiv2>
      </SelectDiv>
    </div>
  );
}

