import { useState } from 'react';
import './App.css';
import styled from "styled-components"

function App() {
  const selectList = ["리액트", "자바스크립트", "next.js", "타입스크립트"];

  const [name, setName] = useState();
  const [money, setMoney] = useState();

  const [isOpen, setIsOpen] = useState(false);

  const [selected, setSelected] = useState();


  const nameChangeHandler = (event) => {
    setName(event.target.value);
  }

  const priceChangeHandler = (e) => {
    const { value } = e.target;
    let str = value.replaceAll(",", "");
    setMoney(str);
  }

  const openModalHandler = () => {
    setIsOpen(!isOpen)
  }

  const SelectHandler = (e) => {
    setSelected(e.target.value);
  }

  const addComma = (price) => {
    let returnString = price?.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
    return returnString;
}

  // 저장누르면 alert 뜨는 버튼
  const clickAlertButtonHandler = (e) => {
  e.preventDefault();
  alert(`{ name : ${name}, price : ${money}}`)

  setName('')
  setMoney('')
  };



  const alertBtn = () => {
    alert('버튼을 만들어보세요');
  }

  const inputAlertBtn = () => {
    prompt('오늘 점심 햄버거');
  }


  return (
    <div>
      <ItemDiv>
        <h1>Button</h1>
        <BtnDiv>
          <PrimaryBtn1 onClick={alertBtn}>
            <div>Large Primary Button
            </div>
          </PrimaryBtn1>
          <PrimaryBtn2>Medium</PrimaryBtn2>
          <PrimaryBtn3>Small</PrimaryBtn3>
        </BtnDiv>
        <BtnDiv>
          <NegativeBtn1 onClick={inputAlertBtn}>Large Negative Button</NegativeBtn1>
          <NegativeBtn2>Medium</NegativeBtn2>
          <NegativeBtn3>Small</NegativeBtn3>
        </BtnDiv>
      </ItemDiv>
      <br/>

      <ItemDiv>
        <h1>Input</h1>
        <div>
          <label>이름</label>
          <input type='text' value={name} onChange={nameChangeHandler}/>
          <label>가격</label>
          <input 
            type='text' 
            value={addComma(money) || ""} 
            placeholder='0'
            onChange={(e)=>priceChangeHandler(e)}/>
          <button onClick={clickAlertButtonHandler}>저장</button>
        </div>
      </ItemDiv>
      <br/>


      <ItemDiv>
        <h1>Modal</h1>
          <ModalContainer>
          <ModalBtn onClick={openModalHandler}
          // 클릭하면 Modal이 열린 상태(isOpen)를 boolean 타입으로 변경하는 메소드가 실행되어야 합니다. 
          > Open Modal
            {/* 조건부 렌더링을 활용해서 Modal이 열린 상태(isOpen이 true인 상태)일 때는 ModalBtn의 내부 텍스트가 'Opened!' 로 Modal이 닫힌 상태(isOpen이 false인 상태)일 때는 ModalBtn 의 내부 텍스트가 'Open Modal'이 되도록 구현 */}
          </ModalBtn>
          {/* 조건부 렌더링을 활용해서 Modal이 열린 상태(isOpen이 true인 상태)일 때만 모달창과 배경이 뜰 수 있게 구현 */}
          {isOpen ? 
          <ModalBackdrop onClick={openModalHandler}>
            //event 버블링을 막는 메소드 
              <ModalView onClick={(e) => e.stopPropagation()}>
                <ExitBtn onClick={openModalHandler}>x</ExitBtn>
                <div className='desc'>HELLO FEJIGU!</div>
              </ModalView>
            </ModalBackdrop>
            : null
          }
        </ModalContainer>
      </ItemDiv>
      <br/>

      <SelectDiv>
        <h1>Select</h1>
        <div>
          <select onChange={SelectHandler} value={selected}>
            {selectList.map((item)=>(
              <option value={item} key={item}>
                {item}
              </option>
            ))
            }
          </select>
          <select onChange={SelectHandler} value={selected}>
            {selectList.map((item)=>(
              <option value={item} key={item}>
                {item}
              </option>
            ))
            }
          </select>
        </div>
      </SelectDiv>
    </div>
  );
}

export default App;

// ItemDiv styled - components
const ItemDiv = styled.div`
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin: 0px 10px 0px 10px;
`

// Button styled - components
const BtnDiv = styled.div`
    display: flex;
    flex-direction: row;
    gap: 10px;
`

const PrimaryBtn1 = styled.button`
    cursor: pointer;
    border-radius: 8px;
    color: rgb(0, 0, 0);
    height: 50px;
    width: 200px;
    border: 3px solid #e0e094;
    background-color: #f5f5dc;
    font-weight: 600;
`

const PrimaryBtn2 = styled.button`
    cursor: pointer;
    border-radius: 8px;
    color: rgb(0, 0, 0);
    height: 45px;
    width: 150px;
    border: none;
    background-color: #f5f5dc;
    font-weight: 600;
`

const PrimaryBtn3 = styled.button`
    cursor: pointer;
    border-radius: 8px;
    color: rgb(0, 0, 0);
    height: 40px;
    width: 100px;
    border: none;
    background-color: #f5f5dc;
    font-weight: 600;
`

const NegativeBtn1 = styled.button`
    cursor: pointer;
    border-radius: 8px;
    color: rgb(0, 0, 0);
    height: 50px;
    width: 200px;
    border: 3px solid #bababa;
    background-color: #e9e9e9;
    font-weight: 600;
`

const NegativeBtn2 = styled.button`
    cursor: pointer;
    border-radius: 8px;
    color: rgb(0, 0, 0);
    height: 45px;
    width: 150px;
    border: none;
    background-color: #e9e9e9;
    font-weight: 600;
`

const NegativeBtn3 = styled.button`
    cursor: pointer;
    border-radius: 8px;
    color: rgb(0, 0, 0);
    height: 40px;
    width: 100px;
    border: none;
    background-color: #e9e9e9;
    font-weight: 600;
`

// Input styled - components

// Modal styled - components
const ModalContainer = styled.div`
  // Modal을 구현하는데 전체적으로 필요한 CSS를 구현
  display : flex;
  justify-content : center;
  align-items : center;
  height : 100%;
`;

const ModalBackdrop = styled.div`
  // Modal이 떴을 때의 배경을 깔아주는 CSS를 구현
  z-index: 1; //위치지정 요소
  position: fixed;
  display : flex;
  justify-content : center;
  align-items : center;
  background-color: rgba(0,0,0,0.4);
  border-radius: 10px;
  top : 0;
  left : 0;
  right : 0;
  bottom : 0;
`;

const ModalBtn = styled.button`
  background-color: var(--coz-purple-600);
  text-decoration: none;
  border: none;
  padding: 20px;
  color: white;
  border-radius: 30px;
  cursor: grab;
`;

const ExitBtn = styled(ModalBtn) `
background-color : #4000c7;
border-radius: 10px;
text-decoration: none;
margin: 10px;
padding: 5px 10px;
width: 40px;
height: 40px;
display : flex;
justify-content : center;
align-items : center;
`;

const ModalView = styled.div.attrs((props) => ({
  // attrs 메소드를 이용해서 아래와 같이 div 엘리먼트에 속성을 추가할 수 있다.
  role: 'dialog',
}))`
  // Modal창 CSS를 구현합니다.
  display: flex;
  align-items: center;
  flex-direction: column;
  border-radius: 20px;
  width: 500px;
  heigth: 200px;
  background-color: #ffffff;
    >div.desc {
      margin: 50px;
      font-size: 20px;
      color: var(--coz-purple-600);
    }
`;


// Select styled - components
const SelectDiv = styled.div`
  width: 700px;
